package com.ifelse;

import java.util.Scanner;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int no=sc.nextInt();
		
		if(no%2==0)
		{
			System.out.println("No is Even");
			return;
		}

			System.out.println("No is odd");
		

	}

}
